def open_options_popup(product_name=None):
    def handle_add_item(item):
        size = item.get("size", "Regular")
        qty = item.get("qty", 1)
        if product_name:
            try:
                if add_item_from_db(product_name, size=size, qty=qty):
                    render_cart()
                    return
            except Exception as exc:
                print(f"Failed to add product from database: {exc}")
        add_item_to_cart(item)

    try:
        show_options_popup(window, on_add_item=handle_add_item, product_name=product_name)
    except Exception as e:
        print(f"Failed to open options popup: {e}")

