def abbreviate_text(text, max_chars=24):
    try:
        if len(text) <= max_chars:
            return text
        return text[: max_chars - 1] + "…"
    except Exception:
        return text

def add_item_to_cart(item):
    try:
        add_item(item)
    except Exception:
        pass
    render_cart()

def change_item_qty_ui(index, delta):
    try:
        change_item_qty(index, delta)
    except Exception:
        pass
    render_cart()

def render_cart():
    # Always read latest shared state
    s = get_state()
    cart_items = s.get("cart_items", [])
    add_on = float(s.get("add_on_total_amount", 0.0))

    # Clear existing item drawings on the cart canvas
    for _id in cart_item_canvas_ids:
        try:
            cart_canvas.delete(_id)
        except Exception:
            pass
    cart_item_canvas_ids.clear()
    cart_item_image_refs.clear()

    # Local layout inside cart_canvas
    left_x = 10
    right_x = 215
    y = 0
    card_height = 70
    v_gap = 14

    # Draw each item card
    for idx, it in enumerate(cart_items):
        try:
            # background card
            bg = cart_canvas.create_rectangle(
                left_x, y, right_x, y + card_height, fill="#ECE3D1", outline="", width=0
            )
            cart_item_canvas_ids.append(bg)

            text_left = left_x + 16

            # Title: Name (Size)
            product_name = abbreviate_text(it.get("name"), 24)
            t1 = cart_canvas.create_text(
                text_left, y + 12, anchor="nw", text=product_name, fill="#1E1E1E",
                font=("Poppins SemiBold", 11 * -1)
            )
            cart_item_canvas_ids.append(t1)

            size_text = f"({it.get('size','Regular')})"
            t_size = cart_canvas.create_text(
                text_left, y + 28, anchor="nw", text=size_text, fill="#6C6C6C",
                font=("Poppins Regular", 10 * -1)
            )
            cart_item_canvas_ids.append(t_size)

            # Price on right
            price_str = f"₱{float(it.get('price',0)):.2f}"
            tprice = cart_canvas.create_text(
                right_x - 16, y + 12, anchor="ne", text=price_str, fill="#1E1E1E",
                font=("Poppins SemiBold", 12 * -1)
            )
            cart_item_canvas_ids.append(tprice)

            # Qty line like: "1  x  ₱29.00" in grey
            qty_line = f"{int(it.get('qty',1))}  x  ₱{float(it.get('price',0)):.2f}"
            t2 = cart_canvas.create_text(
                text_left, y + 44, anchor="nw", text=qty_line, fill="#9A9A9B",
                font=("Poppins Regular", 11 * -1)
            )
            cart_item_canvas_ids.append(t2)

            # Minus and plus simple circle buttons
            minus_circle = cart_canvas.create_oval(
                right_x - 64, y + 44, right_x - 40, y + 68, outline="#9A9A9B", width=2
            )
            minus_line = cart_canvas.create_line(
                right_x - 60, y + 56, right_x - 44, y + 56, fill="#9A9A9B", width=2
            )
            cart_item_canvas_ids.extend([minus_circle, minus_line])

            plus_circle = cart_canvas.create_oval(
                right_x - 32, y + 44, right_x - 8, y + 68, outline="#9A9A9B", width=2
            )
            plus_h = cart_canvas.create_line(
                right_x - 28, y + 56, right_x - 12, y + 56, fill="#9A9A9B", width=2
            )
            plus_v = cart_canvas.create_line(
                right_x - 20, y + 48, right_x - 20, y + 64, fill="#9A9A9B", width=2
            )
            cart_item_canvas_ids.extend([plus_circle, plus_h, plus_v])

            # Bind clicks to adjust qty
            def bind_click(tag_id, cb):
                cart_canvas.tag_bind(tag_id, "<Button-1>", lambda _e: cb())

            bind_click(minus_circle, lambda i=idx: change_item_qty_ui(i, -1))
            bind_click(minus_line,   lambda i=idx: change_item_qty_ui(i, -1))
            bind_click(plus_circle,  lambda i=idx: change_item_qty_ui(i, +1))
            bind_click(plus_h,       lambda i=idx: change_item_qty_ui(i, +1))
            bind_click(plus_v,       lambda i=idx: change_item_qty_ui(i, +1))

        except Exception:
            pass

        y += card_height + v_gap

    # Update scrollable area
    try:
        cart_canvas.configure(scrollregion=(0, 0, 225, y))
    except Exception:
        pass

    # Update totals
    try:
        subtotal = sum(float(it.get('price',0)) * int(it.get('qty',1)) for it in cart_items if not it.get('is_add_on'))
        total = subtotal + float(add_on)
        if subtotal_text_id is not None:
            canvas.itemconfigure(subtotal_text_id, text=f"₱{subtotal:.2f}")
        if add_on_text_id is not None:
            canvas.itemconfigure(add_on_text_id, text=f"₱{add_on:.2f}")
        if total_text_id is not None:
            canvas.itemconfigure(total_text_id, text=f"₱{total:.2f}")
    except Exception:
        pass

def clear_cart():
    try:
        shared_clear_cart()
    except Exception:
        pass
    render_cart()


def open_orders_history():
    global orders_history_window
    if orders_history_window and orders_history_window.winfo_exists():
        try:
            orders_history_window.lift()
            return
        except Exception:
            orders_history_window = None

    orders_history_window = Toplevel(window)
    orders_history_window.title("Order History")
    orders_history_window.geometry("720x420")
    orders_history_window.configure(bg="#FFF8E7")

    try:
        orders_history_window.transient(window)
        orders_history_window.grab_set()
    except Exception:
        pass

    try:
        orders_history_window.update_idletasks()
        w, h = 720, 420
        pw = window.winfo_width()
        ph = window.winfo_height()
        px = window.winfo_rootx()
        py = window.winfo_rooty()
        x = int(px + (pw - w) / 2)
        y = int(py + (ph - h) / 2)
        orders_history_window.geometry(f"{w}x{h}+{x}+{y}")
    except Exception:
        pass

    header = Label(
        orders_history_window,
        text="Order History",
        font=("Poppins Bold", 18),
        bg="#FFF8E7",
        fg="#4A3728",
    )
    header.pack(pady=(20, 10))

    from app.db.connection import db

    customer_id = get_state().get("customer_id")
    if not customer_id:
        info = Label(
            orders_history_window,
            text="No customer information available.\nPlease log in again.",
            font=("Poppins Regular", 12),
            bg="#FFF8E7",
            fg="#4A3728",
            justify="center",
        )
        info.pack(pady=10)
    else:
        orders = db.fetch_customer_orders(customer_id)
        if not orders:
            info = Label(
                orders_history_window,
                text="You have no recorded orders yet.\nPlace an order to see it listed here.",
                font=("Poppins Regular", 12),
                bg="#FFF8E7",
                fg="#4A3728",
                justify="center",
            )
            info.pack(pady=10)
        else:
            columns = ("order_id", "total", "payment", "date", "items")
            tree = ttk.Treeview(
                orders_history_window,
                columns=columns,
                show="headings",
                height=12,
            )
            tree.heading("order_id", text="Order ID")
            tree.heading("total", text="Total Amount")
            tree.heading("payment", text="Payment Method")
            tree.heading("date", text="Date")
            tree.heading("items", text="Items")

            tree.column("order_id", width=80, anchor="center")
            tree.column("total", width=120, anchor="center")
            tree.column("payment", width=160, anchor="center")
            tree.column("date", width=200, anchor="center")
            tree.column("items", width=420, anchor="w")

            for order in orders:
                sale_id = order.get("sale_id")
                items_description = ""
                if sale_id:
                    try:
                        items = db.fetch_sale_items(sale_id)
                        if items:
                            formatted_items = []
                            for item in items:
                                formatted_items.append(
                                    f"{item.get('name') or 'Item'} x{item.get('quantity', 0)} @ ₱{float(item.get('price') or 0.0):.2f}"
                                )
                            items_description = "; ".join(formatted_items)
                    except Exception:
                        items_description = ""
                tree.insert(
                    "",
                    "end",
                    values=(
                        order.get("sale_id"),
                        f"₱{float(order.get('total_amount') or 0.0):,.2f}",
                        str(order.get("payment_method") or "").title(),
                        str(order.get("sale_date") or ""),
                        items_description,
                    ),
                )

            x_scroll = ttk.Scrollbar(
                orders_history_window, orient="horizontal", command=tree.xview
            )
            tree.configure(xscrollcommand=x_scroll.set)
            tree.pack(fill="both", expand=True, padx=20, pady=(10, 0))
            x_scroll.pack(fill="x", padx=20, pady=(0, 20))

    close_btn = Button(
        orders_history_window,
        text="Close",
        font=("Poppins", 11, "bold"),
        bg="#B96708",
        fg="white",
        relief="flat",
        width=12,
        command=orders_history_window.destroy,
    )
    close_btn.pack(pady=(0, 20))

    orders_history_window.resizable(False, False)

def open_checkout_popup():
    try:
        payload = export_for_checkout()
        if not payload:
            messagebox.showinfo("Checkout", "Your cart is empty.")
            return

        cart_items = payload.get("items") if isinstance(payload, dict) else payload
        if not cart_items:
            messagebox.showinfo("Checkout", "Your cart is empty.")
            return

        add_on_total = float(payload.get("add_on_total", 0.0)) if isinstance(payload, dict) else 0.0
        customer_id = payload.get("customer_id") if isinstance(payload, dict) else None
        if customer_id is not None:
            set_current_customer(customer_id)

        run_checkout(cart_items, parent=window, add_on_total=add_on_total, customer_id=customer_id)
    except Exception as exc:
        print(f"Failed to open checkout popup: {exc}")
        messagebox.showerror("Checkout", f"Unable to open checkout window:\n{exc}")
    finally:
        try:
            render_cart()
        except Exception:
            pass

