def _load_products_from_db():
    """Fetch products from the database for display."""
    global _product_rows, _category_names, _current_category_filter
    try:
        raw_products = ProductRepository.get_all_products()
    except Exception as exc:
        print(f"Failed to load products for order screen: {exc}")
        raw_products = []

    formatted = []
    categories = set()
    for product in raw_products:
        name = (
            product.get("product_name")
            or product.get("name")
            or product.get("product_code")
            or "Unnamed Product"
        )
        category = (
            product.get("category_name")
            or product.get("category")
            or "Uncategorized"
        )
        price_regular = float(product.get("price_regular") or product.get("price") or 0.0)
        price_large = float(product.get("price_large") or price_regular)
        formatted.append(
            {
                "product_id": product.get("product_id"),
                "name": name,
                "category": category,
                "price_regular": price_regular,
                "price_large": price_large,
                "image_path": product.get("image_path"),
                "image_blob": product.get("image_blob"),
            }
        )
        categories.add(category)

    _product_rows = formatted
    _category_names = sorted(categories, key=lambda x: x.lower()) if categories else []
    if _current_category_filter != "All":
        if not any(
            c.lower() == _current_category_filter.lower() for c in ["All"] + _category_names
        ):
            _current_category_filter = "All"


def _create_product_photo(product: dict):
    """Return a PhotoImage for the given product."""
    photo = None
    blob = product.get("image_blob")
    if blob:
        try:
            encoded = base64.b64encode(blob).decode("ascii")
            photo = PhotoImage(data="base64," + encoded)
        except Exception:
            photo = None
    if photo is None:
        image_path = product.get("image_path")
        if image_path and os.path.exists(image_path):
            try:
                photo = PhotoImage(file=image_path)
            except Exception:
                photo = None
    if photo is not None:
        _product_photo_refs.append(photo)
    return photo


def render_products(category_filter="All"):
    """Render product grid for the specified category."""
    global _current_category_filter
    _current_category_filter = category_filter or "All"
    for child in products_inner_frame.winfo_children():
        child.destroy()
    _product_photo_refs.clear()

    if not _product_rows:
        Label(
            products_inner_frame,
            text="No products available.",
            bg="#EFE8D8",
            fg="#4A3728",
            font=("Poppins", 12, "italic"),
        ).pack(pady=20)
        products_canvas.yview_moveto(0)
        canvas.itemconfigure(
            category_title_id,
            text="All Products" if _current_category_filter == "All" else _current_category_filter,
        )
        return

    def matches_category(product: dict) -> bool:
        if _current_category_filter == "All":
            return True
        return product.get("category", "").lower() == _current_category_filter.lower()

    filtered = [prod for prod in _product_rows if matches_category(prod)]

    if not filtered:
        Label(
            products_inner_frame,
            text="No products in this category.",
            bg="#EFE8D8",
            fg="#4A3728",
            font=("Poppins", 12, "italic"),
        ).pack(pady=20)
        products_canvas.yview_moveto(0)
    else:
        columns = 4
        for index, product in enumerate(filtered):
            row = index // columns
            col = index % columns
            card = Frame(products_inner_frame, bg="#EFE8D8", bd=0)
            card.grid(row=row, column=col, padx=10, pady=10, sticky="n")

            photo = _create_product_photo(product)
            if photo is not None:
                image_label = Label(card, image=photo, bg="#EFE8D8")
                image_label.pack(pady=(0, 6))
            else:
                name_label = Label(
                    card,
                    text=product["name"],
                    bg="#EFE8D8",
                    fg="#4A3728",
                    font=("Poppins", 11, "bold"),
                    wraplength=120,
                    justify="center",
                )
                name_label.pack(pady=(4, 2))

                price_text = f"₱{product['price_regular']:.2f}"
                if product["price_large"] and abs(product["price_large"] - product["price_regular"]) > 0.01:
                    price_text += f" / ₱{product['price_large']:.2f}"
                price_label = Label(
                    card,
                    text=price_text,
                    bg="#EFE8D8",
                    fg="#1E8449",
                    font=("Poppins", 10),
                )
                price_label.pack(pady=(0, 2))

                status_label = Label(
                    card,
                    text="Available",
                    bg="#EFE8D8",
                    fg="#1C9C5B",
                    font=("Poppins", 10),
                )
                status_label.pack(pady=(0, 6))

            order_button = Button(
                card,
                text="Order",
                font=("Poppins", 9, "bold"),
                bg="#B96708",
                fg="white",
                relief="flat",
                command=lambda name=product["name"]: open_options_popup(name),
            )
            order_button.pack(pady=(6, 0))

    products_canvas.yview_moveto(0)
    canvas.itemconfigure(
        category_title_id,
        text="All Products" if _current_category_filter == "All" else _current_category_filter,
    )


def filter_products(category_name="All", refresh=False):
    """Filter products by category, optionally refreshing from the database."""
    if refresh or not _product_rows:
        _load_products_from_db()
    render_products(category_name or "All")

